mapper.select:
select 

from ownedgame
where steamid = #{steamid,jdbcType=VARCHAR}
  and appid = #{appid,jdbcType=INTEGER}

select

from ownedgame
where steamid = #{steamid,jdbcType=VARCHAR}
and appname like #{appname,jdbcType=VARCHAR}

mapper.delete:
delete from ownedgame
where steamid = #{steamid,jdbcType=VARCHAR}
  and appid = #{appid,jdbcType=INTEGER}
